import {useState,useEffect} from "react";


function EffectTester(){

    const [count,setCount] = useState(0)
    const [color,setColor] = useState("blue")

    useEffect(() => {
       document.querySelector('.countClass').textContent = `Count = ${count}`
    }, [count,color]);

    function plusOneCount(){
        setCount(c=>c+1)
    }
    function  minusOneCount(){
        setCount(c=>c-1)
    }
    function colorChange(){
        setColor(c=>c==='blue'? 'yellow' : 'blue' )
    }


    return <div>

        <button onClick={plusOneCount}>plus one</button>
        <button onClick={minusOneCount}>minus one</button>
        <button onClick={colorChange}>change color</button>
        <h1 style={{color:color}} className={'countClass'}>Count</h1>

    </div>
}

export default EffectTester;
